package com.bornsoft.asn.bean.product;

import java.io.Serializable;

public class ProductCustomProperty implements Serializable {
	private Integer productCPId;
	private Integer productId;
	private String propertyName;
	private String propertyDetail;
	
	public ProductCustomProperty() {
		super();
		// TODO Auto-generated constructor stub
	}

	public ProductCustomProperty(Integer productCPId, Integer productId,
			String propertyName, String propertyDetail) {
		super();
		this.productCPId = productCPId;
		this.productId = productId;
		this.propertyName = propertyName;
		this.propertyDetail = propertyDetail;
	}

	public Integer getProductCPId() {
		return productCPId;
	}

	public void setProductCPId(Integer productCPId) {
		this.productCPId = productCPId;
	}

	public Integer getProductId() {
		return productId;
	}

	public void setProductId(Integer productId) {
		this.productId = productId;
	}

	public String getPropertyName() {
		return propertyName;
	}

	public void setPropertyName(String propertyName) {
		this.propertyName = propertyName;
	}

	public String getPropertyDetail() {
		return propertyDetail;
	}

	public void setPropertyDetail(String propertyDetail) {
		this.propertyDetail = propertyDetail;
	}
	
	
}
